-- Crear la tabla
CREATE TABLE TAB_EXAMENES (
    FECHA_EXAMEN DATE,
    NOMBRE_EXAMEN VARCHAR2(100),
    TIPO_PACIENTE VARCHAR2(50),
    CODIGO_EXAMEN VARCHAR2(20),
    TIPO_CONSULTA VARCHAR2(10),
    EDITAR VARCHAR2(50),
    SUCURSAL NUMBER(2),
    ID_ATENCION NUMBER(10)
);

-- Comentarios de la tabla
COMMENT ON TABLE TAB_EXAMENES IS 'Tabla de registros de exámenes médicos';
COMMENT ON COLUMN TAB_EXAMENES.FECHA_EXAMEN IS 'Fecha del examen';
COMMENT ON COLUMN TAB_EXAMENES.NOMBRE_EXAMEN IS 'Nombre del examen realizado';
COMMENT ON COLUMN TAB_EXAMENES.TIPO_PACIENTE IS 'Tipo de paciente (Hospitalizado, Ambulatorio, etc)';
COMMENT ON COLUMN TAB_EXAMENES.CODIGO_EXAMEN IS 'Código único del examen';
COMMENT ON COLUMN TAB_EXAMENES.TIPO_CONSULTA IS 'Tipo de consulta (LAB, RAY, etc)';
COMMENT ON COLUMN TAB_EXAMENES.SUCURSAL IS 'ID de sucursal';
COMMENT ON COLUMN TAB_EXAMENES.ID_ATENCION IS 'ID de atención médica';

