COMMIT;

-- Mostrar un resumen
SELECT 
    TO_CHAR(FECHA_EXAMEN, 'YYYY-MM') AS MES,
    COUNT(*) AS TOTAL_EXAMENES
FROM TAB_EXAMENES
GROUP BY TO_CHAR(FECHA_EXAMEN, 'YYYY-MM')
ORDER BY MES DESC;

